void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec4 color=vec4(0.0);
	vec2 uv= fc /ir;
    bool mouse_hold = !(iMouse.z < 0.0);
    vec2 mouse = iMouse.xy;
	vec4 last = tx(vec2(0,0));
    if(iFrame<1){
        float dis=distance(uv,vec2(0.5));
        float s=2.0;
        vec2 id=floor(uv*s);
        last.r=1.0;
        //if(fc.x>ir.x/4.0 &&  fc.x <3.0*ir.x/4.0 && fc.y>ir.y/4.0 &&  fc.y <3.0*ir.y/4.0){
        if(uv.x>0.45 && uv.x<0.55 && uv.y>0.45 && uv.y<0.55){
            last.r=0.05;
            last.g=0.5;

        }
        color=last;

    }else{
        vec4 avg=vec4(0);
        for(int i=0;i<8;i++){
            vec2 d=dir8[i];
            avg+=tx(d)*dir8w[i];
        }

        float a=last.r;
        float b=last.g;
        float reaction=a * b * b;

        color.r=a+(((avg.r)-a)*da - reaction + F *(1.0 - a))*dt;
        color.g=b+(((avg.g)-b)*db + reaction - (K+F)*b)*dt;

        //color.r=a+(((avg.r+a)*0.5-a)*da - reaction + F *(1.0 - a))*dt;
        //color.g=b+(((avg.g+b)*0.5-b)*db + reaction - (K+F)*b)*dt;

    }
    float dis=1.0-distance(fragCoord,mouse)/50.0;

    if(mouse_hold && dis>0.5){
       color.g=0.5;
       //color.r=0.5;
    }
    color=clamp(color,vec4(0.0),vec4(1.0));
    fragColor =color ;
}

