#define PI 3.14159265359
#define WAVINESS 100.0
#define MAX_OFFSET 25.0
#define SPEED 0.333

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = mod(iTime * SPEED, 2.0 * PI);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2.0 - vec2(1.0);
    uv.x *= iResolution.x/iResolution.y;

    float cycle = time / (PI * 2.0);
    if (cycle < 0.25)
    {
        cycle *= 4.0 * PI;
        float offset = sin(cycle) * MAX_OFFSET;
        offset = floor(offset);

        if (uv.x > 0.0) uv.y += offset * PI / WAVINESS;
        else uv.y -= offset * PI / WAVINESS;
    }
    else if (cycle < 0.5)
    {
        cycle -= 0.25;
        cycle *= 4.0 * PI;
        float offset = sin(cycle) * MAX_OFFSET;
        offset = floor(offset);

        if (uv.y > 0.0) uv.x += offset * PI / WAVINESS;
        else uv.x -= offset * PI / WAVINESS;
    }
    else if (cycle < 0.75)
    {
        cycle -= 0.5;
        cycle *= 4.0 * PI;
        float offset = sin(cycle) * MAX_OFFSET;
        offset = floor(offset);

        if (uv.x > 0.0) uv.y -= offset * PI / WAVINESS;
        else uv.y += offset * PI / WAVINESS;
    }
    else
    {
        cycle -= 0.75;
        cycle *= 4.0 * PI;
        float offset = sin(cycle) * MAX_OFFSET;
        offset = floor(offset);

        if (uv.y > 0.0) uv.x -= offset * PI / WAVINESS;
        else uv.x += offset * PI / WAVINESS;
    }

    // Time varying pixel color

    float l = length(uv);
    l += PI * 0.5;

    vec3 col = vec3(cos(l * WAVINESS));

    // Output to screen
    fragColor = vec4(col ,1.0);
}
