void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Установка размеров сетки
    const float GRID_SIZE = 30.0;

    // Получение размеров канвы
    vec2 resolution = iResolution.xy;

    // Вычисление размеров каждой части кадра
    vec2 cellSize = resolution / GRID_SIZE;

    // Вычисление индексов текущей части в сетке
    vec2 cellIndex = floor(fragCoord / cellSize);

    // Генерация случайного порядка
    int randomOrder = int(mod(float(cellIndex.y * GRID_SIZE + cellIndex.x) * 345.0, GRID_SIZE * GRID_SIZE));

    // Перестановка индексов
    vec2 randomCellIndex = vec2(mod(float(randomOrder), GRID_SIZE), floor(float(randomOrder) / GRID_SIZE));

    // Смещение текущего фрагмента, основанного на случайном порядке
    vec2 offset = (randomCellIndex - cellIndex) * cellSize;
    vec2 uv = (fragCoord + offset) / resolution;



    // Установка цвета для каждой части
    fragColor = vec4(offset, uv);
}
