void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    uv = 2.*uv - 1.;
    uv.x *= iResolution.x/iResolution.y;
    vec3 scr = vec3(uv, 0.);
    vec3 cam = vec3(0.,0.,-2.);
    vec3 rd = scr - cam;
    rd = normalize(rd);
    float bldp = 2.;
    vec3 pt = vec3(sin(0.2*iTime),cos(0.2*iTime),bldp);

    float mapp = dot(pt, rd);
    mapp = smoothstep(bldp - 0.01, bldp, mapp);
    // Output to screen
    fragColor = vec4(vec3(mapp),1.0);
}
