void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 rayPosition = vec3(.0f, .0f, .0f); // el rayo comienza en la posicion de la camara

    vec3 rayTarget = vec3(fragCoord/iResolution.xy * 2.0f - 1.0f, 1.0f); //uvs de -1 a 1

    vec3 rayDir = normalize(rayTarget- rayPosition);

    fragColor = vec4(rayDir, 1.0f);
}
