void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    float d = length(uv); 
    float angle = atan(uv.y, uv.x) + sin(iTime) * d;
    float squiggle = sin(angle * 5.0) * 0.08;
    float subsquiggle = sin(angle * 10.0) * 0.03;
    d += squiggle + subsquiggle;

    d = sin(d * 8.0 + iTime - sqrt(d) * 8.0) / 8.0;
    d = 0.02 / abs(d);

    vec3 col = vec3(0.5, 0.8, 1.0);
    col *= d;
    col.z = col.z + 0.5 * squiggle + 0.05;

    fragColor = vec4(col, 1.0);
}
