float circle( vec2 p, float r, vec2 uv)
{
    float d = distance(p, uv);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float dist = circle(vec2(0.5, 0.5), 0.5, uv);
    vec3 white = vec3(1.0, 1.0, 1.0);
    //vec3 col = white * float(dist >= 0.01);
    //vec3 col = smoothstep(0.005, .005+(3.0/iResolution.y), dist) *  white;

    float tp = 0.5;
    float f = 0.005;

    vec3 col = smoothstep(tp - f, tp + f, dist) *  white;
    //vec3 col = dist * white;

    fragColor = vec4(col,1.0);
}
