void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 texCol = texture(iChannel0, uv);

    float Cmax = max(texCol.x, max(texCol.y, texCol.z));
    float Cmin = min(texCol.x, min(texCol.y, texCol.z));
    float delta = Cmax - Cmin;

    float H = 0.;
    float S = 0.;
    float V = Cmax;

    if(delta == 0.){
        H = 0.;
    } else if(texCol.x >= texCol.y && texCol.x >= texCol.z){
        H = 60. * mod((texCol.y - texCol.z) / delta, 6.) / 360.;
    } else if(texCol.y >= texCol.x && texCol.y >= texCol.z){
        H = 60. * ((texCol.z - texCol.x) / delta + 2.) / 360.;
    } else if(texCol.z >= texCol.y && texCol.z >= texCol.x){
        H = 60. * ((texCol.x - texCol.y) / delta + 4.) / 360.;
    }


    if(Cmax == 0.){
        S = 0.;
    } else {
        S = delta / Cmax;
    }


    fragColor = vec4(H, S, V, 1.);
}
