#define PI 3.1415926
#define R iResolution.xy
#define W (3./R.y)

float random(vec2 uv) {
    return fract(sin(dot(uv,vec2(12.959,35.137))+12.42)*127.421);
}
float noise(vec2 uv) {
    vec2 ip=floor(uv);
    vec2 fp=fract(uv);
    fp*=fp*(3.-2.*fp);
    float a=random(ip);
    float b=random(ip+vec2(1.,0.));
    float c=random(ip+vec2(0.,1.));
    float d=random(ip+vec2(1.,1.));
    return mix(mix(a,b,fp.x),mix(c,d,fp.x),fp.y);
}

vec3 effect(vec2 uv) {
    vec2 uv0=uv;
    uv.y+=1.;
    float n=noise(uv*vec2(uv.y*150.,2.)-iTime*1.);
    vec3 c=pow(n,6.)*vec3(0.5,0.2,0.8);
    return vec3(c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(fragCoord-R*.5)/R.y;
    vec3 col=effect(uv);
    fragColor=vec4(col,1.0);
}
