float f(vec3 p) 
{
    float a, l, t, i;
    vec3 z = p; 

    for (; ++i < 90.;) 
    {
        z = vec3(dot(z.xy, z.xy) - dot(z.yz, z.yz), 2.0 * z.x * z.y, 2.0 * z.x * z.z) + p;

        float m = dot(z, z);
        if (m > 6.0) break; 

        float w = exp(-.2 * i);
        a += w * exp(-6.5 * pow(abs(m - l), 2.3));
        t += w, l = m;
    }

    return max(0., 5. * a / t - .7);
}

void mainImage(out vec4 o, vec2 u)
{
    u *= 4. / iResolution.y;
    float depth = 2.5; 
    float brightness = 0.65; // 輝度調整パラメータ（0.0～1.0の範囲）

    for(float i; i++<9.; o = tan(.1*(i+u--.yxxx+u.yyxx)))
        u += tanh(13./i*sin(i*u.yx-iTime-19.*atan(u.x,u.y)))/i;

    vec3 R = iResolution, p = (vec3(u, 0) - 1.5 * R) / R.y;
    vec4 newColor = mix(vec4(f(p)), vec4(1.0), 0.5) * mix(vec4(f(p*2.5)), vec4(1.0), 1.5);

    depth -= 1.1 * tanh(u.x); 

    // 輝度の調整を適用
    o = smoothstep(0.3, 0.1, o * 1.9) * depth * newColor * brightness;
}
