void mainImage( out vec4 o, vec2 u )
{
    // Replace u*4. with iResolution.y to get a sphere
    vec3 p = normalize(vec3(u+u-iResolution.xy, u*4.));

    // Use m = .5 to keep starting state
    float t = .2 * iTime,
          d = fract(t),
          m = .5+t-d+smoothstep(.4,.6,d),
          i;

    mat2 M = mat2(cos(t-vec4(0,11,33,0)));
    p.yx *= M;
    p.zx *= M;

    for (o *= 0.; 
         i++ < 12.; 
         o += (1.+cos(1e2*m+vec4(8,7,6,0)))*d*d/15.)
        m *= .85,  
        d = 1.+cos(20.*p.y+t+t)-length(p=1.3*(abs(p-m/4.)-m))/m;

    o *= o;
}
