vec4 getColor(vec2 uv) {

    float angle = atan(uv.y, uv.x);
    float normalizedAngle = (angle + 3.1416) / (1. * 3.146);
    vec3 color1 = vec3(1, 0., 0.0); 
    vec3 color2 = vec3(0); 
    vec3 finalColor = mix(color1, color2, sqrt(normalizedAngle));
    return vec4(sqrt(finalColor),1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - vec2(1.0);
    uv.x *= iResolution.x / iResolution.y;
    uv.y += sin(uv.x *2. + iTime * 5.3) /3.0;
    fragColor = getColor(uv);
}
