void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 texCol = texture(iChannel0, uv);

    float Cmax = max(texCol.x, max(texCol.y, texCol.z));
    float Cmin = min(texCol.x, min(texCol.y, texCol.z));
    float delta = Cmax - Cmin;

    float H = 0.;
    float S = 0.;
    float V = Cmax;

    if(delta == 0.){
        H = 0.;
    } else if(texCol.x >= texCol.y && texCol.x >= texCol.z){
        H = 60. * mod((texCol.y - texCol.z) / delta, 6.);
    } else if(texCol.y >= texCol.x && texCol.y >= texCol.z){
        H = 60. * ((texCol.z - texCol.x) / delta + 2.);
    } else if(texCol.z >= texCol.y && texCol.z >= texCol.x){
        H = 60. * ((texCol.x - texCol.y) / delta + 4.);
    }


    if(Cmax == 0.){
        S = 0.;
    } else {
        S = delta / Cmax;
    }


    // Edit Hue, Saturation, and Value here
    // H = (sin(iTime) + 1.) / 2. * 360.;
    S = (sin(iTime) + 1.) / 2.;
    // V = (sin(iTime) + 1.) / 2. * 1.;


    float C = V * S;
    float X = C * (1. - abs(mod(H / 60., 2.) - 1.));
    float m = V - C;

    vec3 rgbP = vec3(0.);

    if(0. <= H && H < 60.){
        rgbP = vec3(C, X, 0.);
    } else if(60. <= H && H < 120.){
        rgbP = vec3(X, C, 0.);
    } else if(120. <= H && H < 180.){
        rgbP = vec3(0., C, X);
    } else if(180. <= H && H < 240.){
        rgbP = vec3(0., X, C);
    } else if(240. <= H && H < 300.){
        rgbP = vec3(X, 0., C);
    } else {
        rgbP = vec3(C, 0., X);
    }

    vec3 RGB = vec3(rgbP.x + m, rgbP.y + m, rgbP.z + m);

    fragColor = vec4(RGB, 1.);
}
