void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mouse = iMouse.xy / iResolution.xy;
    vec2 uv = fragCoord/iResolution.xy;

    vec3 texCol = vec3(texture(iChannel0, uv));

    float gamma1 = 2.;
    float gamma2 = 1./2.;

    if(uv.x > mouse.x){
        fragColor = vec4(pow(texCol.r, gamma1), pow(texCol.g, gamma1), pow(texCol.b, gamma1), 1.);
    } else {
        fragColor = vec4(pow(texCol.r, gamma2), pow(texCol.g, gamma2), pow(texCol.b, gamma2), 1.);
    }

}
