#define palettea vec3(0.500, 0.500, 0.500)
#define paletteb vec3(0.666, 0.666, 0.666)
#define palettec vec3(1.000, 1.000, 1.000)
#define paletted vec3(0.000, 0.333, 0.667)

vec3 palette(float t) {
    return palettea + paletteb*cos(6.28318*(palettec*t*paletted));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 4.0; i++) {
        uv = fract(uv * 1.5) - 0.5;

        float d = length(uv) * exp(-length(uv0));
        vec3 col = palette(length(uv0) + i*0.1 + iTime);

        d = sin(d*8.0 + iTime)/8.0;
        d = abs(d);
        d = pow(0.01 / d, 2.0);

        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 1.0);
}
