#define TPI 6.283185307
#define OPI 0.159154943

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
        vec2 xy;
        vec2 uv;
        xy.x=0.3*(sin(iTime*1.3))+(fragCoord.x/iResolution.x)-0.5;
        xy.y=0.3*(cos(iTime*1.5))+(fragCoord.y/iResolution.y)-0.5;
        float rot=iTime;
        float vx=atan(xy.x,xy.y)*OPI;
        float mx=length(xy);
        float pe=1.1+sin(rot+(3.0*TPI*vx))*0.5;
        float l=(pe/mx)*0.3;
        uv.x=vx+rot*OPI*0.3;
        uv.y=iTime+l*0.4;
        fragColor = texture(iChannel0, uv);
        fragColor*=l;
}
