#define R iResolution
#define t iTime

void mainImage( out vec4 O, vec2 I )
{
     vec2 u = ( 2.* I - R.xy ) / R.y;
     u += vec2( ( sin( t ) * cos( t ) * 2.2 ) , cos( t ) );
     u *= dot( u * 45. , -u ) * ( 1. - sin( t / 3. ) );
     O.rgb =  vec3( fract( (I - u ) / 54.3 ) , 1.);
}
