#define ITER cos(iTime * 0.3) * 15.
#define BANDPOW 4.

mat2 r2d (float a) {
    float c = cos(a); float s = sin(a);
    return mat2(c,s,-s,c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord /iResolution.xy - 0.5;
    uv.x *= iResolution.x / iResolution.y;
    uv.xy = r2d(iTime * 0.4) * atan(uv.xy);
    uv.xy *= length(sin(1.) / length(0.4*iTime) + mod(length(abs(uv) - sin(iTime * 1.)) - 0.15, 2.));
    uv.y = sin(atan(uv.y, uv.x) * floor(ITER)) * BANDPOW;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));


    // Output to screen
    fragColor = vec4(col,1.0);
}
