void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x += iTime * 0.1;

    vec2 stripe = vec2(cos(radians(30.0)), sin(radians(30.0)));
    stripe = normalize(stripe);
    stripe *= 2.0;
    float stripe_value = mod(dot(uv, stripe), 1.0);

    if (stripe_value > 0.5) {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    } else {
        fragColor = vec4(1.0, 1.0, 0.0, 1.0);
    }
    float middle = step(0.25, uv.y) * step(uv.y, 0.75);
    fragColor = mix(fragColor, vec4(0.0, 0.0, 0.0, 1.0), middle);
}
