void mainImage(out vec4 O, vec2 u) {

    vec2 wavy = vec2(0.5, 1.0);

	u.x += wavy.x*sin(u.y*wavy.y)*6.0;
	u.y += wavy.y*cos(u.x*wavy.x)*8.0;

    u = abs(u+u-(O.xy=iResolution.xy))/O.y;
    O -= O;
    for (float i = 0., t = .5*iTime; i < 50.; O += .001
    /abs(abs(u.x + .75*sin(t+i*.20)) + u.y - 1.0*tan(t+i*0.5))
    *(3.0+sin(i+++vec4(0,1,2,3))));       
}
