float bayer4x4(uvec2 ip) {
    uint x = ip.x, y = ip.y;
    uint t = x^y;
    return float(((t<<3)&8u)|((y<<2)&4u)|(t&2u)|((y>>1)&1u) + 1u) / 18.0;
}

bool fade (float progress, float thres) {
    if (progress > 0.5) thres = 1.0 - thres;
    return progress > thres;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // default color
    fragColor = vec4(0,0,0,0);
    float progress = 0.5 * (sin(fract(iTime * 0.05)*2.0*3.14159265) + 1.0);
    uvec2 ip = uvec2(fragCoord.xy);

    float thres = bayer4x4(ip);


    if (fade(progress, thres)) 
        fragColor = vec4(1,0,0,0);

    if (fade(1.0 - progress, thres)) 
        fragColor = vec4(0,1,0,0);
}
