void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy; // Normalized pixel coordinates (from 0 to 1)
    uv -= .5; //horizontal offset
    uv.x *= iResolution.x/iResolution.y; //correct aspect ratio

    // calculate 3 seperate colour channel patterns
    float r=1.6*tan(cos(7.*uv.x)+cos(7.*uv.y));
    float g=0.8*tan(cos(21.*uv.x)+cos(21.*uv.y));
    float b=1.6-r/2.;

    vec3 col = cos(iTime*1.5+(1.-length(uv))*90.+vec3(r,g,b)); // Time varying pixel color

    fragColor = vec4(col,1.); // Output to screen
}
