void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int it_max = 300;
    float r_max = 2.0;
    float real_min = -0.875;
    float real_max = -0.85;
    float im_min = 0.2;
    float im_max = 0.225;

    vec2 uv = fragCoord/iResolution.xy;
    float z0_re = real_min + uv.x * (real_max - real_min);
    float z0_im = im_min + uv.y * (im_max - im_min);

    float z_re = 0.0;
    float z_im = 0.0;
    float z_abs = 0.0;
    int it = 0;
    while (z_abs <= r_max) {
        if (it == it_max) {
            it = 0;
            break;
        }
        it = it + 1;

        float n_z_re = z_re*z_re - z_im*z_im + z0_re;
        z_im = 2.0*z_re*z_im + z0_im;
        z_re = n_z_re;
        z_abs = sqrt(z_im * z_im + z_re * z_re);
    }

    float g = float(it) / float(it_max);
    fragColor = vec4(g, g, g, 1.0);
}
