void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;


    vec3 col = (length(uv.x)>fract(iTime) || length(uv.y)>fract(iTime)) && !(length(uv.x)>(fract(iTime)+0.1)-floor(fract(iTime)+0.1) || length(uv.y)>(fract(iTime)+0.1)-floor(fract(iTime)+0.1)) ? vec3(56, 163, 165)/255. : vec3(34, 87, 122)/255.;


    fragColor = vec4(col, 1.0);
}
