void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 finalColor = vec3(0.0);

    vec2 dim = vec2(iResolution.xy);

    vec2 uv = fragCoord / dim;
    uv = (2.0 * fragCoord - dim) / dim.y;
    vec2 uvO = uv;

    for (float i = 0.0; i < 5.0; i++) {
        uv = fract(uv * 10.0) - 0.5;
        float d = length(uv) * dot(uvO, vec2(0.5, -1.0) * 10.0);
        d = length(d * uvO);
        d = sin(d * 8.0 - iTime * 2.0) - 0.75;
        d = abs(log(d));

        finalColor += vec3(d);
    }

    fragColor = vec4(finalColor, 1.0);
}
