#define PI 3.141592
 //((p.x >= 0.) ? 1. : -1.)
float dStar(vec2 p)
{
    p.y += 0.1;
    float theta = asin(p.y/length(p));
    if(theta >= -18./180.*PI && theta < 18./180.*PI)  //가운데ooo
        return (((p.x >= 0.) ? -1. : 1.)*tan(36./180.*PI)*p.x+p.y)*PI/4.;
    else if(theta >= 18./180.*PI && theta < 54./180.*PI) //대각선위000
        return p.y;
    else if(theta >= 54./180.*PI && theta < PI/2.)
        return (((p.x >= 0.) ? 1. : -1.)/tan(36./180.*PI)*p.x+p.y)/2.; //위
    else if(theta >= -54./180.*PI && theta < -18./180.) //대각선 아래 ooo
        return (((p.x >= 0.) ? 1. : -1.)*tan(72./180.*PI)*p.x+p.y)/PI;
    else if(theta > -PI/2. && theta < -54./180.*PI) // 밑
        return (((p.x >= 0.) ? 1. : -1.)*tan(36./180.*PI)*p.x+p.y)*PI/4.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pos = (uv - 0.5) *2.;

    pos.x *= iResolution.x/iResolution.y;


    //float d = pos.y/length(pos);
    float d = dStar(pos);

    d = abs(d);

    float x = 10.*d -5.*iTime;

    d = sin(x);

    d = abs(d);

    //d = step(0.5,d);
    //d = 0.01 / d;


    vec3 l[3];
    l[0] = vec3(1,0,0);
    l[1] = vec3(0,1,0);
    l[2] = vec3(0,0,1);
    // Time varying pixel color
    vec3 col = step(0.9, d) == 1. ? l[int(abs(x)/PI) % 3] : vec3(0,0,0);


    fragColor = vec4(col, 1.);
}
