vec3 palette (float t) {
    vec3 a= vec3(0.468, 0.388, 1.538);
    vec3 b= vec3(-0.242, 0.118, 0.028);
    vec3 c= vec3(1.408, 1.268, 2.518);
    vec3 d= vec3(0.000, -3.132, 0.667);

    return a + b*cos(23.*(c*t+d));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y*0.9;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for (float i=0.0; i<2.;i++){

        uv = fract(uv*2.4)- 0.5;

        float d = length (uv)*log(length(uv0)*sin(iTime)+0.1);
        vec3 col = palette(length(uv0)+iTime*0.1+ i*0.4);

        d = (sin(d*8. + iTime))/8.;
        d = abs(d);
        d = pow(0.005 /d ,1.2);

        finalColor += col * d;

        }
    // Output to screen

    fragColor = vec4(finalColor,1.0);
}
