#define PI     3.14159265

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xx;

    vec2 circle = vec2(cos(iTime * 0.4) * 0.5, sin(iTime * 0.4) * 0.5);
    float noise = texture(iChannel0, uv * (1.8 + sin(iTime * 0.1) * .4) + circle).x; 
    uv *= sin(iTime * 0.25);
    uv += vec2(noise);

    float x = length(uv - round(uv)); 
    float dots = smoothstep(0., 0.6 + sin(iTime * .11) * .1, x);

    vec3 col = dots * vec3(
        noise,
        (0.5 + sin(iTime + PI * 2. / 3.) / 2.) * noise,
        (noise + sin(iTime + PI * 4. / 3.) / 2.)
    );

    fragColor = vec4(col,1.0);
}
