void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

   float r= (sin(iTime-uv.y)+2.5)/8.0;
   float rawS= sin(iTime-uv.y*5.0);

    if(r > uv.x){

          fragColor = vec4(0,0,1.0+rawS,1);

    }else if (r < uv.x/2.0){

          fragColor = vec4(1.0+rawS,1.0+rawS,0,1);

    }else{

          fragColor = vec4(1.0+rawS,0,0,1);
 }

}
