#define rnd(v) fract(sin(v))

float noise(in float v){
    float i = floor(v);
    float f = fract(v);

    float u = f * f * (3. - 2. * f);

    return mix(rnd(i), rnd(i+1.), u);
}

float createDrop(in vec2 uv, in float radius){
    float mult = 3.0;
    float speed = 2.0;
    float upperSide = distance(vec2(0.), uv);
    //float downSide = distance(vec2(rnd(iTime*3.0)*0.01),
    //                               uv);

    float downSide = distance(vec2(noise(uv.x*mult+sin(iTime*speed*2.))*0.1, 
                                   noise(uv.y*mult+iTime*speed)*0.28+0.1),
                                   uv);

    float body = mix(upperSide, downSide, pow(1.0-(uv.y+1.0/2.0), 2.0));
    float aa = (1.5/iResolution.y);
    float blur = mix(aa, aa+0.38, pow(1.0-(uv.y+1./2.), 5.));

    return smoothstep(radius + blur, radius, body);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )


{
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;

    float col = 0.;
    col += createDrop(uv, 0.3);

    fragColor = vec4(col);
}
