// Smooth HSV2RGB from https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb(in vec3 c)
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

	return c.z * mix( vec3(1.0), rgb, c.y);
}

//THIS JUST DOES COLORS

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec4 color = vec4(0.5, 0.5, 0.5, 1.0);
    // Loop over all boids
    vec4 bcolor = vec4(0.0, 0.0, 0.0, 1.0);
    bool hit = false;
    for (int i = 0; i < NUM_BOIDS; i++) {
        ivec2 tex_pos = ivec2(i % int(iChannelResolution[0].x),
                              i / int(iChannelResolution[0].x));
        vec4 pos_vel = texelFetch(iChannel0, tex_pos, 0);
        vec4 pos_vel_z = texelFetch(iChannel1, tex_pos, 0);
        vec3 pos = vec3(pos_vel.xy, pos_vel_z.x);
        vec3 vel = vec3(pos_vel.zw, pos_vel_z.z);

        vec2 proj = pos.xy;

        if (distance(fragCoord, proj) < (BOIDS_SIZE / pos.z) && 
            BOIDS_SIZE / pos.z <= MAX_SIZE) {
                vec3 rgb = hsv2rgb(vec3((float(i) + 0.5) / float(NUM_BOIDS), 1.0, 0.9));
                bcolor = vec4(rgb, 1.0);
                hit = true;
        }


    }
    if (hit) {
        color = bcolor;
    }

    // Output to screen
    fragColor = color;
}


/*

The trick I used to turning boids 3D was to keep a separate buffer to return only the z position
and velocity. I incorporated Z's velocity such that boids closer to the camera would be bigger, 
and boids further from the camera would be smaller.


*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // Initialization //

    if (iFrame == 0) {
        vec4 pos_vel = texture(iChannel2, fragCoord/iChannelResolution[2].xy);
        pos_vel.xy = iResolution.xy / 2.0 + (pos_vel.xy - 0.5) * iResolution.xy * 0.2;
        pos_vel.zw = (pos_vel.zw - 0.5) * iResolution.xy * 1.0;
        fragColor = pos_vel;
        return;
    }

    ////////////////////


    ivec2 iFragCoord = ivec2(fragCoord); 
    vec4 pos_vel_xy = texelFetch(iChannel0, iFragCoord, 0);
    vec4 pos_vel_z = texelFetch(iChannel1, iFragCoord, 0);
    vec3 pos = vec3(pos_vel_xy.xy, pos_vel_z.x);
    vec3 vel = vec3(pos_vel_xy.zw, pos_vel_z.z);
    // ID of the current Boid
    int my_id = int(iFragCoord.y) * int(iResolution.x) + int(iFragCoord.x);

    vec3 F_separation = vec3(0.0, 0.0, 0.0);
    vec3 F_cohesion = vec3(0.0, 0.0, 0.0);
    vec3 F_alignment = vec3(0.0, 0.0, 0.0);
    vec3 F_boundary = vec3(0.0, 0.0, 0.0);

    int safety_boids = 0;
    int visible_boids = 0;

    vec3 p_avg = vec3(0.0, 0.0, 0.0);
    vec3 v_avg = vec3(0.0, 0.0, 0.0);

    int a_a = 0;
    int a_b = 0;
    int b_a = 0;
    int b_b = 0;
    int c_a = 0;
    int c_b = 0;

    for (int i = 0; i < int(iChannelResolution[0].x); i++) {

        if (i != my_id) {

            ivec2 tex_pos_i = ivec2(i % int(iChannelResolution[0].x),
                                    i / int(iChannelResolution[0].x));
            vec4 pos_vel_xy_i = texelFetch(iChannel0, tex_pos_i, 0);
            vec4 pos_vel_z_i = texelFetch(iChannel1, tex_pos_i, 0);
            vec3 pos_i = vec3(pos_vel_xy_i.xy, pos_vel_z_i.x);
            vec3 vel_i = vec3(pos_vel_xy_i.zw, pos_vel_z_i.z);

            if (distance(pos, pos_i) < dist_safety) {

                safety_boids += 1;
                F_separation += pos - pos_i;

            }

            if (distance(pos, pos_i) < dist_visible) {

                visible_boids += 1;
                p_avg += pos_i;
                v_avg += vel_i;

            }

        }

    }

    if (pos.x < dist_left) {
        a_a = 1;
    }
    if (pos.x > dist_right) {
        a_b = 1;
    }
    if (pos.y < dist_top) {
        b_a = 1;
    }
    if (pos.y > dist_bottom) {
        b_b = 1;
    }
    if (pos.z < dist_near) {
        c_a = 3;
    }
    if (pos.z > dist_far) {
        c_b = 3;
    }

    F_separation = F_separation / float(safety_boids);
    F_cohesion = (p_avg / float(visible_boids)) - pos;
    F_alignment = (v_avg / float(visible_boids)) - vel;
    F_boundary = vec3( float(a_a - a_b), float(b_a - b_b), float(c_a - c_b) );


    vec3 F = a_separation * F_separation + 
             a_cohesion * F_cohesion +
             a_alignment * F_alignment + 
             a_boundary * F_boundary; 

    vel += F;
    pos += vel;

    if ( length(vel) > 0.0 ) {

        if ( length(vel) > speed_max ) {  
            vel = vel * (speed_max / length(vel));
        }
         if ( length(vel) < speed_min ) {  
            vel = vel * (speed_min / length(vel));
        }

    }

    fragColor = vec4(pos.xy, vel.xy);
}

