vec3 palette(float t) {
    vec3 a = vec3(0.5, 0.3, 0.7) * cos(t * 5.0) + vec3(0.5);
    vec3 b = vec3(0.5, 0.7, 0.3) * sin(t * 5.0) + vec3(0.5);
    vec3 c = vec3(0.7, 0.5, 0.3) * cos(t * 5.0) + vec3(0.5);
    vec3 d = vec3(0.3, 0.7, 0.5) * sin(t * 5.0) + vec3(0.5);
    return a + b * cos(6.28318 * (c * t + d));
}

// Función para crear patrones geométricos con agujeros negros
vec3 geometricPattern(vec2 uv, float time) {
    vec3 color = vec3(0.0);
    float angle = atan(uv.y, uv.x);

    // Patrones de círculos
    float mainCircles = cos(12.0 * length(uv) - angle * 10.0 * time);
    float extraCircles1 = cos(15.0 * length(uv - vec2(0.3, 0.3)) - angle * 8.0 * time);
    float extraCircles2 = cos(18.0 * length(uv + vec2(0.3, 0.3)) - angle * 6.0 * time);
    float extraCircles3 = cos(20.0 * length(uv + vec2(-0.3, -0.3)) - angle * 4.0 * time);
    float extraCircles4 = cos(22.0 * length(uv - vec2(-0.3, 0.3)) - angle * 7.0 * time);

    // Combinar patrones
    float combinedPattern = mainCircles + 0.5 * (extraCircles1 + extraCircles2 + extraCircles3 + extraCircles4);

    // Crear "agujeros negros"
    float blackHoles = smoothstep(0.2, 0.3, abs(sin(time + length(uv) * 5.0)));
    combinedPattern *= blackHoles;

    // Aplicar paleta de colores
    color = palette(combinedPattern + time);
    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;
    vec3 finalColor = vec3(0.0);
    float fastTime = iTime * 2.0; // Aumentar la velocidad del tiempo

    // Aplicar el patrón geométrico
    finalColor += geometricPattern(uv, fastTime);

    fragColor = vec4(finalColor, 1.0);
}
