// from https://iquilezles.org/articles/distfunctions
float udRoundBox( vec2 p, vec2 b, float r )
{
    return length(max(abs(p)-b+r,0.0))-r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    // setup
    float t = 0.3+ 0.02 * sin(iTime- 0.5);
    float iRadius = min(iResolution.x, iResolution.y) * (0.05 + t);
    vec2 halfRes = 0.5 * iResolution.xy;

    // compute box
    float b = udRoundBox( (fragCoord.xy - halfRes)*1.5, halfRes, iRadius );

    b = (0.003+sin(iTime+uv.x*3.)*0.002)/(abs(b)/iResolution.x);

	vec3 c = mix( vec3(1.0,0.0,0.0), vec3(0.0,0.0,0.0), smoothstep(0.0,1.0,b) );

    fragColor = vec4( b );
}
