void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = fragCoord/iResolution.y;
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 uv2 = fract(20.0f*uv);
    float t = fract(0.8*iTime);
    float zoom  = mix(2.0,-2.0,t)+mix(1.0f,-1.0f,uv.x);
    if(DiamondMask(uv2,zoom)<0.5f)
    {
        col *=0.0f;
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

vec4 someFunction( vec4 a, float b )
{
    return a+b;
}

float DiamondMask(vec2 uv, float zoom)
{
	float x0 = min(uv.x, 1.0 - uv.x);

    zoom *=0.5f;

	// Upper half of the shape.
	float y0 = 0.5 - x0 - zoom;

	// Lower half of the shape.
	float y1 = 1.0 - y0;

	// Shade area inside shape.
	if (uv.y > y0 && uv.y < y1) {
		return 1.0f;
	}
    else
    {
        return 0.0f;
    }
}
