void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv *= 400.0;

    if (iMouse.z >= 1.0) {
        uv /= mod(iTime, 14.0);
    }

    vec2 id = floor(uv);
    uv = fract(uv);

    float r = 0.0, g = 1.0 * mod(id.x + id.y, 2.0), b = 1.0 * (1.0 - mod(id.x + id.y, 2.0));

    vec3 M = iMouse.xyz / iResolution.xyz;
    if (M.x > 0.6) {
        r = b;
        b = 0.0;
    } else if (M.x > 0.3) {
        r = g;
        g = 0.0;
    }

    fragColor = vec4(vec3(r, g, b),1.0);
}
