void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 res = vec2(98.5,102.8);
    vec2 uv = floor(fragCoord/res);

    float timeScale = 60.;

    uv.y *= 13.;
    float uv1 = uv.x + uv.y;

    fragColor = vec4(vec3(round(fract((iTime * timeScale)*pow(2.,uv1-91.)))),1.0);
}
