float d(float a) { return fract(iTime * a) * 3.141593 * 4.; }
mat2 h(float a) {
  float b = sin(a), c = cos(a);
  return mat2(c, b, -b, c);
}
float f(vec3 a) {
  float b = 5.;
  a.yz *= h(.314159 - sin(d(.05)) * .2), a.xz *= h(cos(d(.05)) * .2),
      a.y += cos(a.x) * .15, a.y += cos(a.y) * .15, b = length(a) - 2.5;
  float c = length(a.xy) * 1., e = atan(a.y, a.x),
        g = cos(e + cos(c - d(.05)) * 15. - d(.05)) * .25;
  b += g * .15;
  return b;
}

vec3 k(in vec3 b) {
  vec2 a = vec2(1, -1) * .5773;
  return normalize(a.xyy * f(b + a.xyy * 5e-4) + a.yyx * f(b + a.yyx * 5e-4) +
                   a.yxy * f(b + a.yxy * 5e-4) + a.xxx * f(b + a.xxx * 5e-4));
}
vec3 l(vec3 q, vec3 a) {
  float g = 0., i = 0.;
  vec3 j = normalize(vec3(.57703));
  j.xz *= h(d(.05));

  vec3 m = normalize(j - a),
       c = vec3(.7 + .3 * cos(d(.05) + a + vec3(1, 2, 4))), e = vec3(0);
  for (int n = 0; n < 64; n++) {
    e = q + a * g, i = f(e);
    g += i;
  }
  if (i < 1.) {
    vec3 b = k(e);
    float o = dot(b, b), p = pow(clamp(dot(b, m), 0., 1.), 50.) * .6;
    p *= o;
    vec3 r = reflect(b, a);
    return p * .8 +
           (.5 + .35 * cos(o + d(.05) * 3. + a.xyx * 2. + vec3(0, 2, 4))) * .2 +
           texture(iChannel0, r).rgb * .5;
  }
  c = sqrt(c), c = sqrt(c), c *= .25;
  return c;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 a = fragCoord.xy / iResolution.xy;
  a = (a - .5) * 2., a.x *= iResolution.x / iResolution.y;
  vec3 b = vec3(0, 0, -4), c = vec3(a, 1), e = l(b, c);
  fragColor = vec4(e, 1);
}
