vec2 mainSound( int samp, float time )
{
    // A 440 Hz wave that attenuates quickly overt time
    float harmonic = floor(fract(time/4.0)*16.0)+1.0;
    float frequency = mod(time*2.0, 110.0);
    return vec2(sin((acos(0.0)*4.0)*frequency*time*harmonic)+(fract(time*frequency)))/2.0;
    }
