float red_line(vec2 uv)
{
    float time_speed = 0.5;
    float time = iTime * time_speed;

    float perspective_warp = 10.0;

    return smoothstep(
            0.02 * abs(uv.x * perspective_warp),
            0.0,
            fract( (abs(uv.x) - time)) * abs(uv.x / perspective_warp)
           );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.0);

    vec3 red_color = vec3(1.0, 0.0, 0.0);

    col = mix(col, red_color, red_line(uv));

    fragColor = vec4(col,1.0);
}
