precision mediump float;


float TWO_PI = 6.2831852;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 st = (fragCoord.xy * 2. - iResolution.xy);
  vec3 color_fin = vec3(0.);

  float time = iTime * 1000.;
  float duration = 2.;
  float startAngle = 0.;
  float cycle = duration * 1000.;
  float cycleIndex = 1. - floor(time / cycle);
  float cycleProgress = fract(time / cycle);

  vec3 color_ring = vec3(0.13, 0.27, 0.28);
  vec3 color_act = vec3(0.0, 0.96, 0.83);

  float d = length(st);

  float w = iResolution.y * 0.2;
  float ring = smoothstep(w + 4., w, abs(d - iResolution.y * 0.7));

  float gapNum = 12.;
  float gapSize = TWO_PI / (gapNum * 1.);

  float theta = mod(atan(-st.x, st.y)+startAngle, TWO_PI);

  float gap = mod(floor(theta / gapSize),2.);
  float ring2 = ring;// - gap;

  float theta2 = theta - gapSize * (mod(cycleIndex, gapNum * 1.)+0.0);
  float progress = ring2 * (step(0., theta) - step(0., theta2));
  float progress2 = ring2 * (step(0., theta2) - step(0., theta2 - gapSize));

  color_fin += ring2 * color_ring;
  progress += progress2 * (clamp(mod(20.,(1.-cycleProgress*1.5)*19.),0.,1.));
  color_fin = mix(color_fin, color_act, progress);

  fragColor = vec4(color_fin, 1.0);
}
