vec2 barrelDistortion ( vec2 coord, float amt ) {
	vec2 cc = coord - 0.5;
	float dist = dot(cc, cc);
	//return coord + cc * (dist*dist)  * amt;
	return coord + cc * dist * amt;
}

void mainImage ( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = ( fragCoord.xy / iResolution.xy * 0.5f ) + 0.25f;

    vec4 accum = vec4( 0.0f );
    const float iterations = 40.0f;
    for ( float i = 0.0f; i < iterations; i++ ) {
        accum += texture( iChannel0, barrelDistortion( uv, 0.2f * i ) );
    }

	fragColor = vec4( accum.rgb / iterations, 1.0f );
}
