vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return abs(fract((a1)*(a2.x+a2.y))-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 16.,c1=0.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv.xy += iTime/t1/12./2.;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    float scale = 1.5;
    vec2 t2 = vec2(0.);
    vec3 col1 = col;
    for(int k = 0; k < 9; k++){

        uv.y -= 1./4.;

        uv = (uv+t2)/scale;

        uv = 1.+(fract(vec2(uv+vec2(.5,1.5))*scale)-.5)/scale;

        //WOW!
        //uv += fract(t2/2.)/2.;

        //uv = (fract(vec2(uv+vec2(.5,1.5))*scale)-.5)/scale;

        t2 = triangle_wave(uv-.5);
        uv =
            (t2-triangle_wave(uv.yx))
        ;
        c1 =
            max(abs(uv.y+uv.x*sign(t2.y-t2.x)),c1)
        ;
        c1 =
            abs(2.*c1-1.)
            //1.-col.x-c1
            //col.x
            //1.-abs(2.*c1-1.);
        ;
        col.x =
            //c1
            max(max(length(uv-t2-c1),abs(uv.y-uv.x))/3.,col.x)
        ;

        col =
            abs(col-(1.-(c1*col.x)));
        col1 =
            abs(col1*c1-col-1.).yzx;
    }
    fragColor = vec4(col1/2.,1.0);
}
