#define root sqrt(2.0)
#define pi 3.14159265359

float gradient(float value, float weight){
    value *= pi;
    value = max(0.0, min(pi, value));
    value = 1.0/2.0 + 1.0/2.0*cos(value - weight*pi/2.0);
    return value;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // pixel coordinates
    vec2 uv = (fragCoord/iResolution.xy - 0.5)*30.0;
    uv.x *= iResolution.x/iResolution.y;
    uv = mod(abs(uv)+(sin(iTime/root)+1.0)*5.0, cos(iTime-iMouse.y/100.0)*pi+10.0-length(uv));
    uv *= mat2(cos(iTime/2.0), sin(iTime/2.0), -sin(iTime/2.0), cos(iTime/2.0));
    uv += iMouse.x/50.0;

    // time varying pixel color
    vec3 col = vec3(sin(iTime + sin(uv.x) + cos(uv.y)));

    // output to screen
    fragColor = vec4(gradient(col[0], iTime+length(uv)/3.0), 
                     gradient(col[1], iTime/pi+uv.x/3.0/root), 
                     gradient(col[2], iTime/root+uv.y/3.0/pi),
                     1.0);
}
