//Color Palette 

vec3 palette( in float t)
{
    vec3 a = vec3(0.5,0.5,0.5);
    vec3 b = vec3(0.5,0.5,0.5);
    vec3 c = vec3(1.0,1.0,1.0);
    vec3 d = vec3(0.1,0.2,0.3);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy-vec2(0.5,0.5))*2.0;
         uv.x *= (iResolution.x/iResolution.y);

    vec2 uv0 = uv;


    float count = 5.0;

    vec3 finalColor = vec3(0.0);
    for(float i = 0.0; i<count; i++) {  
         uv = fract(uv*2.0)-0.5;

    float d = (length(uv))*2.5+exp(length(uv0));
          d = 2.0*cos(d-iTime*0.2);
          d = sin(5.0*d+iTime*1.0);

    vec3 col = palette(length(uv0)+i*0.5+iTime);
         col = col*pow(d,1.0);
         finalColor += col;
    }

    finalColor*=1./(count/2.0);

    // Time varying pixel color



    // Output to screen
    fragColor = vec4(finalColor,1.0);
}
