void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 pos = (uv.xy-0.5);
    vec3 color = vec3(1.0, 0.66, 1.0);

    float r = 0.3 + tan(atan( pos.y, pos.x) * 1.0 * iTime);

    vec3 draw = color * smoothstep(r * cos(iTime), r + 0.1, length(pos));

	fragColor = vec4(draw,1.0);
}
