void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    // Normalized pixel coordinates from 0 to 1
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0.5, 1.618, 4.236));

    // Add random fractal
    col += 0.2 * fract(sin(dot(uv.xy ,vec2(12.9898,78.233))) * 43758.5453);

    // Zoom in and move
    vec2 center = vec2(0.5, 0.5) + 0.5 * sin(iTime);
    vec2 offset = uv - center;
    float zoom = 0.5 + 0.5 * sin(iTime * 0.5);
    vec2 uv2 = center + zoom * offset;

    // Add exploding colors and designs
    col += 0.2 * fract(sin(dot(uv2.xy ,vec2(12.9898,78.233))) * 43758.5453);
    col += 0.2 * fract(sin(dot(uv2.xy ,vec2(12.9898,78.233))) * 43758.5453);
    col += 0.2 * fract(sin(dot(uv2.xy ,vec2(12.9898,78.233))) * 43758.5453);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
