#define time iTime * -4.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv *= 2.0;
    uv -= 1.0;
    uv.x *= iResolution.x/iResolution.y;

    vec2 uv2 = uv * 20.0;
    uv2 = fract(uv2);

    uv2 *= 2.0;
    uv2 -= 1.0;

    vec2 dir = normalize(uv);
    float l = length(uv) * 25.0;

    float w = sin(l + time);
    float w2 = sin(l + time + 1.62);
    uv2 += dir * w;

    vec3 col = vec3(smoothstep(0.2, 0.15, length(uv2)));

    float lrp = smoothstep(-1., 1., w2);
    col *= mix(vec3(1.0, 0.0, 0.0), vec3(0.0, 0.0, 1.0), lrp);

    fragColor = vec4(col,1.0);
}
