// Not trying to make this bit small,
// but I am trying to make the contents of Buf. A small

#define mouseDrag (texelFetch(iChannel0, ivec2(0, 0), 0).xy)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 mo = (mouseDrag * 2.0 - iResolution.xy) / iResolution.y;

    fragColor = texture(iChannel1, (uv - mo));
    fragColor += vec2(smoothstep(0.1, 0.05, length(uv - mo)), 1).xxxy;
}

// Can it get smaller?

// -2 by MysteryPancake:
//  -1 d.xy = iFrame > 0 ? r(p.x) : vec2(0);
//  -1 by removing trailing zeroes if(iMouse.w > 0.)

#define r(i) texelFetch(iChannel0, ivec2(i, 0), 0).xy
#define w(i, t) if (i == p.x) d.rg = t;

void mainImage(out vec4 d, vec2 c)
{
    ivec2 p = ivec2(c);
    d.xy = iFrame > 0? r(p.x) : vec2(0);

    if(iMouse.w > 0.)
    {
        w(1, r(0));
    }
    else
        w(0, r(1) + iMouse.xy - abs(iMouse.zw));
}

