float weight[5] = float[] (0.227027, 0.1945946, 0.1216216, 0.054054, 0.016216);

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // 计算uv, iResolution.xy是screen大小
    vec2 uv = fragCoord.xy / iResolution.xy;
    // 模拟像素
    vec2 pixed = 2.0 / iResolution.xy;
    // 相邻像素的uv
    vec2 texcoords[24];
    texcoords[0] = uv + vec2(-1.0, 0.0) * pixed;
    texcoords[1] = uv + vec2(0.0, -1.0) * pixed;
    texcoords[2] = uv + vec2(1.0, 0.0) * pixed;
    texcoords[3] = uv + vec2(0.0, 1.0) * pixed;
    texcoords[4] = uv + vec2(-1.0, -1.0) * pixed;
    texcoords[5] = uv + vec2(-1.0, 1.0) * pixed;
    texcoords[6] = uv + vec2(1.0, -1.0) * pixed;
    texcoords[7] = uv + vec2(1.0, 1.0) * pixed;
    texcoords[8] = uv + vec2(-2.0, 0.0) * pixed;
    texcoords[9] = uv + vec2(2.0, 0.0) * pixed;
    texcoords[10] = uv + vec2(0.0, 2.0) * pixed;
    texcoords[11] = uv + vec2(0.0, -2.0) * pixed;
    texcoords[12] = uv + vec2(-2.0, -1.0) * pixed;
    texcoords[13] = uv + vec2(-2.0, 1.0) * pixed;
    texcoords[14] = uv + vec2(-1.0, -2.0) * pixed;
    texcoords[15] = uv + vec2(-1.0, 2.0) * pixed;
    texcoords[16] = uv + vec2(1.0, -2.0) * pixed;
    texcoords[17] = uv + vec2(1.0, 2.0) * pixed;
    texcoords[18] = uv + vec2(2.0, -1.0) * pixed;
    texcoords[19] = uv + vec2(2.0, 1.0) * pixed;
    texcoords[20] = uv + vec2(-2.0, -2.0) * pixed;
    texcoords[21] = uv + vec2(-2.0, 2.0) * pixed;
    texcoords[22] = uv + vec2(2.0, -2.0) * pixed;
    texcoords[23] = uv + vec2(2.0, 2.0) * pixed;
    // 相邻像素对应的权重, 卷积核
    float w[24];
    w[0] = w[1] = w[2] = w[3] = weight[0];
    w[4] = w[5] = w[6] = w[7] = weight[1];
    w[8] = w[9] = w[10] = w[11] = weight[2];
    w[12] = w[13] = w[14] = w[15] = w[16] = w[17] = w[18] = w[19]= weight[3];
    w[20] = w[21] = w[22] = w[23] = weight[4];
    // 权重总和, 用来归一化卷积核
    float totalWeight = w[0] * 4.0 + w[4] * 4.0 + w[8] * 4.0 + w[12]*8.0 + w[20]*4.0;
    // 卷积运算
    vec4 gaussColor = vec4(0.0);
    for (int i = 0; i < 24; i++) {
        gaussColor += texture(iChannel0, texcoords[i]) * w[i] / totalWeight;
    }
    // 原图
    vec4 originalColor = texture(iChannel0, uv);
    // sin + time动态变化
    float normalTime =0.7;// abs(sin(iTime));
    // mix输出颜色
    fragColor = mix(originalColor, gaussColor, normalTime);

}
