vec3 palette(float x, vec3 dcOff, vec3 ampl, vec3 freq, vec3 phase)
{


    return dcOff + ampl * cos(3.141592 * 2.0 * (freq * x + phase));


}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= (iResolution.x / iResolution.y);


    vec3 dcOff = vec3(0.55, 0.55, 0.55);
    vec3 ampl = vec3 (0.1, 0.1, 0.1);
    vec3 freq = vec3(2.5, 2.5, 2.5);
    vec3 phase = vec3(0.1, 2.0, 1.0);

    vec3 color = vec3(0.0);

    color += palette(sin(sin(uv.x) - tan(uv.y) + iTime), dcOff, ampl, freq, phase);

    phase = vec3(0.1 + iTime / 1.5, 0.2 + iTime / 3.5, 0.3 + iTime / 4.0);
    color += palette(cos(sin(uv.y - uv.x) - tan(uv.y) + iTime), dcOff, ampl, freq, phase);

    phase = vec3(0.4 + iTime / 3.5, 0.2 + iTime / 4.5, 0.3 + iTime / 5.0);
    color -= palette(sin(cos(uv.y + cos(uv.x + iTime) + iTime) - tan(uv.y) + iTime), dcOff, ampl, freq, phase);

    color.r = pow(color.r, 2.0);
    color.g = pow(0.45 / color.g, 2.0);
    color.b = pow(1.0 / color.b, 2.0);

    fragColor = vec4(color, 1.0);


}
